import React, { Component } from 'react';
import { FaBook, FaSearch } from 'react-icons/fa';
import { Link } from 'react-router-dom';
import PropTypes from 'prop-types';
import Container from '../../components/container/index';
import StringCompare from 'string-similarity';
import { Form, List } from './styles';
export default class Search extends Component {
  state = {
    newSearch: '',
    repositories: [],
    loading: false,
  };

  componentDidMount() {
    const repositories = localStorage.getItem('books');
    console.log(books);
    if (books) {
      this.setState({ repositories: JSON.parse(books) });
    }
  }

  render() {
    const { repositories } = this.state;
    return (
      <Container>
        <h1>
          <Link to="/">
            <FaBook />
            Books
          </Link>
        </h1>
        <h1>
          <FaSearch />
          Pesquisar
        </h1>
        <Form>
          <input type="text" placeholder="Search by title" />
        </Form>
        <List>
          {repositories.map(book => (
            <li key={book.title}>
              {/* <img src="" alt=""/> */}
              <span>{book.title}</span>
              <span>{book.author}</span>
              <span>{book.pages}</span>
              {book.read ? <span>Read</span> : <span>Unread</span>}
              <Span>
                {book.read ? (
                  <button
                    type="button"
                    onClick={() => this.handleChangeRead(book)}
                  >
                    Unread
                  </button>
                ) : (
                  <button
                    type="button"
                    onClick={() => this.handleChangeRead(book)}
                  >
                    Read
                  </button>
                )}
                <button type="button" onClick={() => this.handleDelete(book)}>
                  Remove
                </button>
              </Span>
            </li>
          ))}
        </List>
      </Container>
    );
  }
}
